mapper.select:
SELECT * FROM ( SELECT ROW_NUMBER() OVER(ORDER BY r_num DESC) as r_no, r_num, r_title, r_statNm, r_content, r_writer, r_like, r_time, r_rank FROM review_board
AND r_title LIKE CONCAT('%', #{reviewVO.keyword}, '%') AND r_content LIKE CONCAT('%', #{reviewVO.keyword}, '%')
AND r_writer = #{reviewVO.keyword}
AND r_statNm = #{reviewVO.keyword}
) r2 WHERE r2.r_no BETWEEN #{start} AND #{end} AND r2.r_num IS NOT NULL

SELECT COUNT(*) FROM review_board


		 AND r_title LIKE
			CONCAT('%', #{keyword}, '%') 

			AND r_content LIKE CONCAT('%', #{keyword}, '%')

		 AND r_writer = #{keyword} 

			AND r_statNm = #{keyword}

mapper.insert:
UPDATE review_board
SET r_like = r_like + 1
WHERE r_num = #{r_num}

mapper.select:
SELECT r_like
FROM review_board
WHERE r_num = #{r_num}

SELECT AVG(r_rank) AS average
FROM review_board
where r_statid = #{r_statid}
